invoice.scss:
body {
    background: #fff;
    color: #000;
    margin: 10px;
}

body,
td,
th {
    font-family: Arial, Sans-Serif;
    font-size: 12px;
}

h1,
h1 a {
    color: #000;
    text-decoration: none;
}

.Invoice,
.PackingSlip {
    border: 2px solid #cacaca;
    padding: 5px;
}

.InvoiceTitle,
.PackingSlipTitle {
    background: #000;
    color: #fff;
    font-size: 15px;
    font-weight: bold;
    margin-bottom: 10px;
    padding: 5px;
}

.StoreAddress {
    font-weight: bold;
    margin-bottom: 10px;
}

.InvoiceHeading,
.PackingSlipHeading {
    font-size: 15px;
    font-weight: bold;
    margin-bottom: 10px;
}

.CustomerDetails,
.AddressRow,
.InvoiceItems,
.InvoiceDetails,
.PackingSlipItems,
.PackingSlipDetails {
    margin-bottom: 10px;

    li {
        list-style-type: none;
    }
}

.InvoiceTable,
.PackingSlipTable {
    border-collapse: collapse;
    width: 100%;
}

.InvoiceTable th,
.PackingSlipTable th {
    font-weight: bold;
    padding: 5px;
    text-align: left;
}


.InvoiceTable td,
.PackingSlipTable td {
    padding: 5px;
    text-align: left;
    vertical-align: top;
}

td.ProductQuantity,
th.ProductQuantity,
td.ProductCost,
th.ProductCost,
td.ProductTotal,
th.ProductTotal,
.InvoiceTotals td,
.InvoiceTotalRow td {
    text-align: right;
}

.InvoiceTotalRow:last-child {
    font-weight: bold;
}

.AddressRow,
.InvoiceDetails,
.PackingSlipDetails {
    overflow: hidden;
    width: 100%;
}

.ShippingAddress,
.BillingAddress,
.InvoiceDetailsLeft,
.InvoiceDetailsRight,
.PackingSlipDetailsLeft,
.PackingSlipDetailsRight {
    float: left;
    width: 48%;

    ul {
        padding-left: 0;
    }
}

.NewAddress {
    border-top: 1px solid #000;
}

.InvoiceItems,
.PackingSlipItems {
    border-top: 1px solid #000;
    padding-top: 10px;
}

.InvoiceItemList {
    border-bottom: 1px solid #000;
}

.InvoiceItemDivider td {
    border-top: 1px solid #000;
    padding-top: 10px;
}

.DetailRow,
.ConfigurableProductRow {
    clear: left;
    margin-top: 6px;
    padding-left: 140px;
}

.DetailRow .Label,
.ConfigurableProductRow .Label {
    display: inline;
    float: left;
    margin: 0 0 6px -140px;
    padding-top: 1px;
    position: relative;
    width: 130px;

}

.DetailRow .Value,
.ConfigurableProductRow .Value {
    display: inline;
}

.InvoiceDetails .DetailRow .Label,
.PackingSlipDetails .DetailRow .Label {
    font-weight: bold;
}

.ConfigurableProductRow {
    font-size: 11px;
    margin-left: 10px;
}

.InvoiceTotals .InvoiceTotal td {
    font-weight: bold;
}

.ProductQuantity {
    width: 50px;
}

.ProductCost,
.ProductTotal {
    width: 150px;
}

.PageBreak {
    page-break-after: always;
}

.ProductPreOrder {
    font-size: 11px;
}

assets/scss/theme.scss:
// =============================================================================
// STENCIL
// =============================================================================


// Tools
// -----------------------------------------------------------------------------
//
// 1. Import Citadel's toolkit for use.
//
// -----------------------------------------------------------------------------

@import "../../node_modules/@bigcommerce/citadel/dist/tools/toolkit"; // 1
@import "tools/tools";

// Settings
// -----------------------------------------------------------------------------
//
// 1. Stencil global settings get imported first.
// 2. Import all Citadel and Foundation settings.
// 3. Common aria helpers.
// 4. Import Stencil's component settings overrides.
// 5. Import tools which set/reset Citadel's global settings, to be consumed by
//    the rest of Stencil.
// 6. Import Citadel's version of foundation.
//      - This enables the ability to "null" variables in the Stencil settings.
//
// -----------------------------------------------------------------------------

@import "settings/global/global"; // 1

@import "../../node_modules/@bigcommerce/citadel/dist/settings/global/global"; // 2

@import "settings/global/tools"; // 5

@import "../../node_modules/@bigcommerce/citadel/dist/settings/normalize/normalize"; // 2
@import "../../node_modules/@bigcommerce/citadel/dist/settings/foundation/foundation"; // 2
@import "../../node_modules/@bigcommerce/citadel/dist/settings/bigcommerce/bigcommerce"; // 2

@import "../../node_modules/@bigcommerce/citadel/dist/vendor/foundation/foundation"; // 6

@import "settings/normalize/normalize"; // 4
@import "settings/vendor/vendor"; // 4
@import "settings/foundation/foundation"; // 4
@import "settings/citadel/citadel"; // 4
@import "settings/stencil/stencil"; // 4
@import "settings/layouts/layouts"; // 4


// Utilities
// -----------------------------------------------------------------------------

@import "utilities/utilities";
@import "../../node_modules/@bigcommerce/citadel/dist/utilities/utilities";


// Components
// -----------------------------------------------------------------------------
//
// 1. Import Citadel's normalize dependency.
// 2. Import Citadel's components.
// 2. Import Stencil's component additions and custom components.
//
// -----------------------------------------------------------------------------

@import "../../node_modules/@bigcommerce/citadel/dist/vendor/normalize/normalize"; // 1
@import "../../node_modules/@bigcommerce/citadel/dist/components/components"; // 2
@import "common/index"; // 3
@import "components/components"; // 6


// Layouts
// -----------------------------------------------------------------------------
//
// 1. Stencil design layout files (example: Header or Footer).
//
// -----------------------------------------------------------------------------

@import "layouts/layouts"; // 1
button.button.button--primary {
    margin-top: 22px !important;
}
a.button.dropdown-menu-button {
    color: #000 !important;
}
a.button.button--small.button--action {
